import React from 'react';
import {TouchableOpacity, Button, TextInput, StyleSheet, Text, View } from 'react-native';
import { add } from 'react-native-reanimated';
import {AppStyles } from '../src/AppStyles'
import Navigator from './navigation/Navigator';



export default class FirstPage extends React.Component {
            state={
            email:"",
            password:""
          }
     render(){
    return (

        <View style={styles.container}>
            <Text style={[styles.title, styles.centreTitle]} >Welcome to Visa Conscierge Services</Text>
                    <View style={styles.inputView} >
                      <TextInput  
                        style={styles.inputText}
                        placeholder="Email..." 
                        placeholderTextColor="#003f5c"
                        onChangeText={text => this.setState({email:text})}/>
                    </View>
                    <View style={styles.inputView} >
                      <TextInput  
                        style={styles.inputText}
                        placeholder="Email..." 
                        placeholderTextColor="#003f5c"
                        onChangeText={text => this.setState({email:text})}/>
                    </View>


                <TouchableOpacity style={styles.loginBtn} 
                onPress={
                        () => {
                            navigation.navigate('Login');
                        }
                    }
                >
              <Text style={styles.loginText}>LOGIN</Text>
                </TouchableOpacity>


            <View style={styles.buttonStyle}>
                <Button
                containerStyle={styles.loginContainer}
                    title="Login"
                    style={styles.loginText}
                    onPress={
                        () => {
                            props.navigation.navigate('Login');
                        }
                    } />
                <Text style={[styles.title,styles.centreTitle]}>OR</Text>

                <Button
                    title="Register"
                    style={styles.loginText}
                    containerStyle={styles.facebookContainer}
                    onPress={
                        () => {
                            props.navigation.navigate('Register');
                        }
                    } />
            </View>

        </View>
    );
}
};



const styles = StyleSheet.create({
    container: {
    flex: 1,
    alignItems: "center",
    backgroundColor:"#192061",
    justifyContent: 'center'
  },
  logo:{
    fontWeight:"bold",
    fontSize:50,
    color:"#fb5b5a",
    marginBottom:40
  },
  or: {
    fontSize:30,
    alignItems:"center",
    color: "black",
    marginTop: 20,
    marginBottom: 20
  },
  title: {
    fontSize: AppStyles.fontSize.title,
    fontWeight: "bold",
    color: AppStyles.color.tint,
    marginTop: 20,
    marginBottom: 20
  },
  leftTitle: {
    alignSelf: "stretch",
    textAlign: "left",
    marginLeft: 20
  },
  centreTitle:{
    alignSelf: "stretch",
    textAlign: "center",
    marginLeft: 20
  },
  content: {
    paddingLeft: 50,
    paddingRight: 50,
    textAlign: "center",
    fontSize: AppStyles.fontSize.content,
    color: AppStyles.color.text
  },
  loginContainer: {
    width: AppStyles.buttonWidth.main,
    backgroundColor: AppStyles.color.tint,
    borderRadius: AppStyles.borderRadius.main,
    padding: 10,
    marginTop: 30
  },
  loginText: {
    color: AppStyles.color.white
  },
  placeholder: {
    fontFamily: AppStyles.fontName.text,
    color: "red"
  },
  InputContainer: {
    width: AppStyles.textInputWidth.main,
    marginTop: 30,
    borderWidth: 1,
    borderStyle: "solid",
    borderColor: AppStyles.color.grey,
    borderRadius: AppStyles.borderRadius.main
  },
  body: {
    height: 42,
    paddingLeft: 20,
    paddingRight: 20,
    backgroundColor:"white",
    color: AppStyles.color.text

  },
  facebookContainer: {
    width: AppStyles.buttonWidth.main,
    backgroundColor: AppStyles.color.facebook,
    borderRadius: AppStyles.borderRadius.main,
    padding: 10,
    marginTop: 30
  },
    inputView:{
    width:"80%",
    backgroundColor:"#465881",
    borderRadius:25,
    height:50,
    marginBottom:20,
    justifyContent:"center",
    padding:20
  },
  facebookText: {
    color: AppStyles.color.white
  },
   loginBtn:{
    width:"80%",
    backgroundColor:"#fb5b5a",
    borderRadius:25,
    height:50,
    alignItems:"center",
    justifyContent:"center",
    marginTop:40,
    marginBottom:10
  },
